mapper.select:
select * from (select rownum rn, ap_form_name, ap_doc_no, ap_doc_title, user_name as ap_Doc_Writer_Name, ap_doc_writer,ap_doc_project_name, ap_doc_createdate, ap_doc_enddate , ap_doc_status from (select f.ap_form_name, d.ap_doc_no, d.ap_doc_title, u.user_name, d.ap_doc_writer, d.ap_doc_project_name, d.ap_doc_createdate, d.ap_doc_enddate , d.ap_doc_status from approvaldoc d join approvalform f on d.ap_form_no = f.ap_form_no join users u on d.ap_doc_writer = u.user_id where ap_doc_writer = #{apDocWriter} and ap_doc_status in (0, 2)
order by ap_doc_status desc, ap_doc_no)
where rownum <= #{cri.pageNum} * #{cri.amount}) where rn > (#{cri.pageNum} -1) * #{cri.amount}

select Max(ap_doc_no)
from approvaldoc
where ap_form_no = #{apFormNo}
and ap_doc_writer = #{apDocWriter}

select NVL(count(*),0)
from approvaldoc
where ap_doc_writer = #{apDocWriter}
and ap_doc_status = #{apDocStatus}

mapper.update:
update approvaldoc
set ap_doc_step = ap_doc_step+1
where ap_doc_no = #{apDocNo}

update approvaldoc
set ap_doc_step = ap_doc_step+1, ap_doc_enddate = sysdate, ap_doc_status = 1
where ap_doc_no = #{apDocNo}

update approvaldoc
set ap_doc_step = -1, ap_doc_status = 2
where ap_doc_no = #{apDocNo}

mapper.select:
select ap_start_date, ap_end_date
from approvaldate
where ap_doc_no = #{apDocNo}
