mapper.select:
select NVL(count(*),0)
from approvaldoc
where ap_doc_writer = #{apDocWriter} and ap_doc_status = #{apDocStatus}

mapper.update:
update approvaldoc
set ap_doc_step = ap_doc_step+1
where ap_doc_no = #{apDocNo}

update approvaldoc
set ap_doc_step = ap_doc_step+1, ap_doc_enddate = sysdate, ap_doc_status = 1
where ap_doc_no = #{apDocNo}

update approvaldoc
set ap_doc_step = -1, ap_doc_status = 2
where ap_doc_no = #{apDocNo}
